import React from "react";
import HoverVideoPlayer from "react-hover-video-player";
//styles
import styled from "styled-components";
import { devices } from "../breakpoints";
//svgs
import { ReactComponent as CasesNext } from "../assets/arrow-right.svg";
import { ReactComponent as CasesPrev } from "../assets/arrow-left.svg";
// data
import casesArr from "../data";

console.table(casesArr);
function Cases() {
    const onMouseOver = (e) => {
        console.log(e);
    };
    const PausedOverlay = () => (
        <div>
            <img
                src='https://commondatastorage.googleapis.com/gtv-videos-bucket/sample/images/BigBuckBunny.jpg'
                alt=''
            />
            <div
                className={css`
                    /* Ensure the description text is displayed on top of the thumbnail image */
                    z-index: 1;
                    font-family: sans-serif;
                    /* Position the text in the bottom-left corner of the overlay */
                    position: absolute;
                    bottom: 0;
                    left: 0;
                    padding: 1em;

                    h1 {
                        margin: 0 0 0.2em;
                    }
                    p {
                        margin: 0 0.2em 0;
                    }
                `}
            >
                <h1>Big Buck Bunny</h1>
                <p>Hover to play!</p>
            </div>
        </div>
    );

    return (
        <Case>
            <ContainerFluid>
                <CasesNavigation>
                    <CasesArrow className='next'>
                        <CasesPrev />
                    </CasesArrow>
                    <CasesArrow className=' prev  disabled'>
                        <CasesNext />
                    </CasesArrow>
                </CasesNavigation>
                <Row>
                    <CaseContainer>
                        {casesArr.map((cases) => (
                            <CaseInfoContainer key={cases.id}>
                                <CaseInfo>
                                    <span>{cases.title}</span>
                                    <h2>{cases.type}</h2>
                                </CaseInfo>
                                <CaseImage>
                                    <HoverVideoPlayer
                                        videoSrc={`${cases.img}`}
                                        pausedOverlay={
                                            <img
                                                src='thumbnail-image.jpg'
                                                alt=''
                                                style={{
                                                    // Make the image expand to cover the video's dimensions
                                                    width: "100%",
                                                    height: "100%",
                                                    objectFit: "cover",
                                                }}
                                            />
                                        }
                                    />
                                    {/* <video
                                        src={`${cases.img}`}
                                        type='video/mp4'
                                        // controls
                                        // muted
                                        // autoPlay={"autoplay"}
                                        // loop
                                    /> */}
                                </CaseImage>
                            </CaseInfoContainer>
                        ))}
                    </CaseContainer>
                </Row>
            </ContainerFluid>
        </Case>
    );
}

const Case = styled.section`
    /* border: 12px solid green; */
    height: 50vh;
    height: calc(var(--vh, 1vh) * 50);
`;
const ContainerFluid = styled.div`
    /* border: 1px solid red; */
`;
const CasesNavigation = styled.div`
    /* border: 2px solid purple; */
    position: absolute;
    bottom: 0px;
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;

    box-sizing: border-box;
    z-index: 6;
    padding: 32px;
    @media ${devices.mobileL} {
        display: none;
    }
`;
const CasesArrow = styled.div`
    /* border: 1px solid orange; */
    background-color: rgba(0, 0, 0, 0.4);
    height: 72px;
    width: 72px;
    border-radius: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    svg {
        color: #fff;
        height: 28px;
        width: 28px;
    }
    &.disabled {
        opacity: 0.3;
    }
`;
const Row = styled.div`
    /* border: 1px solid blue; */
`;
const CaseContainer = styled.div`
    /* border: 1px solid blue; */
    display: flex;
    @media ${devices.mobileL} {
        flex-direction: column;
    }
`;

const CaseInfoContainer = styled.div`
    /* border: 1px solid orange; */
    position: relative;
    background: #000;
    cursor: pointer;
    transition: all 0.5s ease-in-out;
    &:hover {
        video {
            opacity: 0.4;
        }
    }
`;
const CaseInfo = styled.div`
    /* border: 1px solid blue; */
    width: 25vw;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 32px;
    z-index: 1;
    position: relative;
    height: calc(var(--vh, 1vh) * 90);
    @media ${devices.mobileL} {
        padding: 16px;
    }
    span {
        margin-top: 156px;
        font-size: 1.6rem;
        opacity: 0.8;
        color: #fff;
        font-weight: 600;
        @media ${devices.mobileL} {
            font-size: 1.2rem;
            line-height: 2.2rem;
            width: 250px;
        }
    }
    h2 {
        font-size: 2.4rem;
        line-height: 3.4rem;
        width: 85%;
        margin-top: 16px;
        color: #fff;
        @media ${devices.mobileL} {
            font-size: 1.7rem;
            line-height: 2.2rem;
            margin-top: 8px;
            width: 250px;
        }
    }
`;
const CaseImage = styled.div`
    /* border: 1px solid blue; */
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
    position: absolute;
    opacity: 0.65;
    transition: 0.4s cubic-bezier(0.6, -0.05, 0.1, 0.99);

    video {
        width: 100%;
        height: 100%;
        object-fit: cover;
        transition: all 0.5s ease-in-out;
    }

    audio::-webkit-media-controls-timeline,
    video::-webkit-media-controls-timeline {
        display: none;
    }
    audio::-webkit-media-controls,
    video::-webkit-media-controls {
        display: none;
    }
`;

export default Cases;
